export default {
  data() {
    return {
      //获取路由中的参数
      id: parseInt(this.$route.params.id),
      product: null,
      product_goods: 0,
      begood: true,
      bestar: true,
      shop_stars: 0
    };
  },
  methods: {
    async getProductLikes() {
      await this.axios.get('get_product_likes/', {
        params: {
          product_id: this.id
        }
      }).then(response => {
        console.log(response);
        this.product_goods = response.data.goods;
      }).catch(function (error) {
        console.log(error);
      });
      await this.axios.get('get_shop_stars/', {
        params: {
          business_id: this.product.fields.product_business
        }
      }).then(response => {
        console.log(response);
        this.shop_stars = response.data.stars;
      }).catch(function (error) {
        console.log(error);
      });
    },
    async changeGood() {
      if (this.begood) {
        this.product_goods -= 1;
      } else {
        this.product_goods += 1;
      }
      this.begood = !this.begood;
      await this.axios.get('toggle_user_like_to_product/', {
        params: {
          user_id: this.$store.state.userId,
          product_id: this.id
        }
      }).then(response => {
        console.log(response);
      }).catch(function (error) {
        console.log(error);
      });
    },
    async changeStar() {
      if (this.bestar) {
        this.shop_stars -= 1;
      } else {
        this.shop_stars += 1;
      }
      this.bestar = !this.bestar;
      await this.axios.get('toggle_user_star_to_shop/', {
        params: {
          user_id: this.$store.state.userId,
          business_id: this.product.fields.product_business
        }
      }).then(response => {
        console.log(response);
      }).catch(function (error) {
        console.log(error);
      });
    },
    async initUserToProduct() {
      await this.axios.get('get_user_like_to_product/', {
        params: {
          user_id: this.$store.state.userId,
          product_id: this.id
        }
      }).then(response => {
        console.log(response);
        //this.list = response.data.list
        if (response.data.good === 'good') {
          this.begood = true;
        } else {
          this.begood = false;
        }
      }).catch(function (error) {
        console.log(error);
      });
      await this.axios.get('get_user_star_to_shop/', {
        params: {
          user_id: this.$store.state.userId,
          business_id: this.product.fields.product_business
        }
      }).then(response => {
        console.log(response);
        //this.list = response.data.list
        if (response.data.star === 'star') {
          this.bestar = true;
        } else {
          this.bestar = false;
        }
      }).catch(function (error) {
        console.log(error);
      });
    },
    async getProduct(x1) {
      await this.axios.get('fetch_product/', {
        params: {
          product_id: x1
        }
      }).then(response => {
        console.log(response);
        this.product = response.data.product;
      }).catch(function (error) {
        console.log(error);
      });
    },
    async handleAddCart() {
      await this.axios.get('add_to_cart/', {
        params: {
          user_id: this.$store.state.userId,
          product_id: this.id
        }
      }).then(response => {
        console.log(response);
        //this.list = response.data.list
        window.alert("添加成功");
      }).catch(function (error) {
        console.log(error);
      });
    }
  },
  async created() {
    //初始化数据
    console.log("detailview");
    console.log(this.id);
    await this.getProduct(this.id);
    console.log("detailviewend");
    console.log(this.product);
    await this.initUserToProduct();
    await this.getProductLikes();
  },
  async mounted() {
    //初始化数据
    console.log("11detailview");
    console.log(this.id);
    await this.getProduct(this.id);
    console.log("11detailviewend");
    console.log(this.product);
  }
};
